n    int value;\\n    childNodeT *cn;\\n\\n    if (t != NULL) {\\n        cn = (childNodeT *) ((char *)t + top-&gt;userSize); \\/\\/ \\u6307\\u9488\\u8fd0\\u7b97\\n\\n        value = *(int *)t;\\n        printf(\\"%d\\\\n\\", value);\\n        RecMapBST(top, cn-&gt;left);\\n        RecMapBST(top, cn-&gt;right);\\n    }\\n}\\n\\nint main() {\\n    btType *bt;\\n    childNodeT *childData;\\n\\n    bt = (btType*)malloc(sizeof(btType));\\n    bt-&gt;node = NULL;\\n    bt-&gt;userSize = sizeof(int);\\n    bt-&gt;totalSize = bt-&gt;userSize + sizeof(btType);\\n\\n    bt-&gt;node = malloc(bt-&gt;totalSize);\\n    *((int*)bt-&gt;node) = 2;\\n    childData = (childNodeT *) ((char *)bt-&gt;node + bt-&gt;userSize); \\/\\/ \\u6307\\u9488\\u8fd0\\u7b97\\n    childData-&gt;left = NULL;\\n    childData-&gt;right = NULL;\\n\\n    childNodeT *tmp;\\n    childNodeT *childData2;\\n    childData2 = malloc(bt-&gt;totalSize);\\n    *((int*)childData2) = 5;\\n    tmp = (childNodeT *) ((char *)childData2 + bt-&gt;userSize); \\/\\/ \\u6307\\u9488\\u8fd0\\u7b97\\n    tmp-&gt;left = NULL;\\n    tmp-&gt;right = NULL;\\n    childData-&gt;left = childData2; \\/\\/ \\u7ed9\\u5de6\\u5b69\\u5b50\\u8d4b\\u503c\\n\\n    childNodeT *childData3;\\n    childData3 = malloc(bt-&gt;totalSize);\\n    *((int*)childData3) = 9;\\n    tmp = (childNodeT *) ((char *)childData3 + bt-&gt;userSize); \\/\\/ \\u6307\\u9488\\u8fd0\\u7b97\\n    tmp-&gt;left = NULL;\\n    tmp-&gt;right = NULL;\\n    childData-&gt;right = childData3; \\/\\/ \\u7ed9\\u53f3\\u5b69\\u5b50\\u8d4b\\u503c\\n\\n    MapBST(bt);\\n}<\\/code><\\/pre>\\n<p>\\u4ee5\\u4e0a\\u4ee3\\u7801\\u53d6\\u540d\\u4e3a c_pointer_2.c \\uff0c\\u7f16\\u8bd1\\u6267\\u884c\\u5982\\u56fe\\uff1a<br \\/>\\n<img src=\\"http:\\/\\/www.shishao.site\\/storage\\/article\\/2021\\/07\\/17\\/2Bt0JKbafULbPuia4o4oqWHIOZQMznNzoehUcWR6.png\\" alt=\\"image.png\\" \\/><\\/p>\\n<ul>\\n<li>Go\\u6307\\u9488\\u7684\\u7075\\u6d3b\\u4f53\\u73b0\\u5728\\u7ed3\\u6784\\u4f53\\u4e0a\\uff0cGo\\u