rt.c redisassert.h\\n    $(CC) $(CFLAGS) -c redisassert.c\\n\\nsds.o: sds.c sds.h\\n    $(CC) $(CFLAGS) -c sds.c\\n\\nzmalloc.o: zmalloc.c zmalloc.h\\n    $(CC) $(CFLAGS) -c zmalloc.c\\n\\ndict.o: dict.c dict.h  siphash.c\\n    $(CC) $(CFLAGS) -c dict.c\\n\\nskiplist.o: skiplist.c skiplist.h\\n    $(CC) $(CFLAGS) -c skiplist.c\\n\\n$(REDISLIB):$(OBJS)\\n    -rm -f $(REDISLIB)\\n    ar cr $(REDISLIB) $(OBJS)\\n    ranlib $(REDISLIB)\\n\\n# ***************************************************************\\n# Entry to reconstruct the gccr script\\n\\ngccr: makefile\\n    @echo \'#! \\/bin\\/csh -f\' &gt; gccr\\n    @echo \'set INCLUDE =\' `pwd` &gt;&gt; gccr\\n    @echo \'set REDISLIB = $$INCLUDE\\/libredis.a\' &gt;&gt; gccr\\n    @echo \'set LIBRARIES = $$REDISLIB\' &gt;&gt; gccr\\n    @echo \'foreach x ($$*)\' &gt;&gt; gccr\\n    @echo \'  if (\\"x$$x\\" == \\"x-c\\") then\' &gt;&gt; gccr\\n    @echo \'    set LIBRARIES = \\"\\"\' &gt;&gt; gccr\\n    @echo \'    break\' &gt;&gt; gccr\\n    @echo \'  endif\' &gt;&gt; gccr\\n    @echo \'end\' &gt;&gt; gccr\\n    @echo \'gcc -g -I$$INCLUDE $$* $$LIBRARIES\' &gt;&gt; gccr\\n    @chmod a+x gccr\\n    @echo \'[gccr script created]\'\\n\\nclean:\\n    rm -f *.o\\n    rm -f $(BINS)\\n    rm -f $(REDISLIB)\\n    rm -f gccr<\\/code><\\/pre>\\n<p>makefile \\u662f\\u6211\\u4eec\\u6bcf\\u6b21\\u628a\\u8981\\u62bd\\u53d6\\u7684\\u4ee3\\u7801\\u5b8c\\u6210\\u540e\\uff0c\\u91cd\\u70b9\\u8981\\u7f16\\u5199\\u7684\\u6587\\u4ef6\\uff0c\\u6240\\u4ee5\\u5982\\u679c\\u6ca1\\u80fd\\u638c\\u63e1 makefile \\u6587\\u4ef6\\u7684\\u5c0f\\u4f19\\u4f34\\u4eec\\u5f97\\u52a0\\u628a\\u52b2\\uff0c\\u81f3\\u5c11\\u80fd\\u505a\\u5230\\u4f9d\\u846b\\u82a6\\u753b\\u74e2\\uff0c\\u8fd9\\u6837\\u4f60\\u5c31\\u80fd\\u62c6\\u89e3\\u4ee3\\u7801\\u81ea\\u5df1\\u628a\\u9759\\u6001\\u5e93\\u751f\\u6210\\u51fa\\u6765\\u3002<\\/p>\\n<p>\\u628a makefile \\u6587\\u4ef6\\u505a\\u597d\\u540e\\uff0c\\u5728 04-skiplist \\u76ee\\u5f55\\u4e0b\\u6267\\u884c <code>make<\\/code> \\u547d\\u4ee4\\uff0c\\u521b\\u5efa\\u51fa libredis.a \\u9759\\u6001\\u5e93\\u6587\\u4ef6\\u4e0e gccr \\u811a\\u672c\\u6587\\u4ef6\\u3002<\\/p>\\n<h3>\\u8df3\\u8dc3\\u8868\\u7684\\u63a5\\u53e3\\u6d4b\\u8bd5<\\/h3>\\n<p>\\u6d4b\\u8bd5\\u6837\\u4f8b\\u4e00\\uff1a<\\/p>\\n<pre><code class=\\"language-c\\">#include&lt;stdio.h&gt;\\n#include\\"skiplist.h\\"\\n\\nint main() {\\n    zskiplist *sl;\\n    zskiplistNode *sln;\\n    zskiplistNode **dsln;\\n    sds s, s_temp, spe;\\n\\n    s = sdsnew(\\"aaa\\");\\n    sl = zslCreate();\\n    sln = zslInsert(sl, 25, s);\\n\\n    spe = sdsdup(s);\\n    sln = zslInsert(sl, 55, spe);\\n\\n    spe = sdsnew(\\"bigist\\");\\n    sln = zslInsert(sl, 95, spe);\\n\\n    s_temp = sdsdup(s);\\n    sln = zslInsert(sl, 15, s_temp);\\n\\n    s_temp = sdsdup(s);\\n    sln = zslInsert(sl, 35, s_temp);\\n\\n    s_temp = sdsnew(\\"good\\");\\n    sln = zslInsert(sl, 50, s_temp);\\n\\n    zrangespec range = { .min = 0, .max = 95, .minex = 0, .maxex = 0};\\n    sln = zslFirstInRange(sl, &amp;range);\\n    while(sln != NULL) {\\n        printf(\\"element: %s, score: %.2f\\\\n\\", sln-&gt;ele, sln-&gt;score);\\n\\n        zslDelete(sl, sln-&gt;score, sln-&gt;ele, NULL);\\n        sln = zslFirstInRange(sl, &amp;range);\\n    }\\n\\n    zslFree(sl);\\n}<\\/code><\\/pre>\\n<p>\\u5176\\u4e2d <code>zrangespec<\\/code> \\u7ed3\\u6784\\u542b\\u4e49\\u5982\\u4e0b\\uff1a<\\/p>\\n<pre><code>typedef struct {\\n    \\/\\/ \\u6700\\u5c0f\\u503c\\u548c\\u6700\\u5927\\u503c\\n    double min, max;\\n    \\/\\/ \\u6307\\u793a\\u6700\\u5c0f\\u503c\\u548c\\u6700\\u5927\\u503c\\u662f\\u5426\\u4e0d\\u5305\\u542b\\u5728\\u8303\\u56f4\\u4e4b\\u5185\\n    \\/\\/ \\u503c\\u4e3a 1 \\u8868\\u793a\\u4e0d\\u5305\\u542b\\uff0c\\u503c\\u4e3a 0 \\u8868\\u793a\\u5305\\u542b\\n    int minex, maxex; \\n} zrangespec;<\\/code><\\/pre>\\n<p>\\u4e0a\\u9762\\u7684\\u4f8b\\u5b50\\uff0cmax = 95 \\u7b49\\u4e8e\\u6700\\u5927 score 95\\uff0c\\u6240\\u4ee5\\u8fd0\\u884c\\u7ed3\\u679c\\u4e2d\\u51fa\\u73b0 95\\u3002\\u8fd0\\u884c\\u7684\\u8f93\\u51fa\\uff1a<\\/p>\\n<pre><code class=\\"language-shell\\"># ..\\/gccr skiplist_test.c -o skiplist_test.o\\n# .\\/skiplist_test.o\\nelement: aaa, score: 15.00\\nelement: aaa, score: 25.00\\nelement: aaa, score: 35.00\\nelement: good, score: 50.00\\nelement: aaa, score: 55.00\\nelement: bigist, score: 95.00<\\/code><\\/pre>\\n<p>\\u5982\\u679c\\u628a maxex \\u4ece 0 \\u6539\\u4e3a 1\\uff0c\\u8fd0\\u884c\\u7ed3\\u679c\\u7684\\u8f93\\u51fa\\u4f1a\\u662f\\uff1a<\\/p>\\n<pre><code class=\\"language-shell\\"># ..\\/gccr skiplist_test.c -o skiplist_test.o\\n# .\\/skiplist_test.o\\nelement: aaa, score: 15.00\\nelement: aaa, score: 25.00\\nelement: aaa, score: 35.00\\nelement: good, score: 50.00\\nelement: aaa, score: 55.00<\\/code><\\/pre>\\n<p>\\u6216\\u8005\\u628a max \\u6539\\u6210 90\\uff0c\\u7ed3\\u679c\\u4f1a\\u662f\\u4ec0\\u4e48\\uff1f<\\/p>\\n<h3>\\u7ed3\\u5c3e<\\/h3>\\n<p>\\u6bcf\\u4e2a\\u6570\\u636e\\u62c6\\u89e3\\u5176\\u5b9e\\u5e76\\u4e0d\\u96be\\uff0c\\u53ea\\u8981\\u6709\\u8010\\u5fc3\\uff0c\\u62a5\\u5565\\u9519\\u8bef\\u5c31\\u6309\\u9519\\u8bef\\u63d0\\u793a\\u53bb\\u89e3\\u51b3\\u5c31\\u53ef\\u4ee5\\u5b9e\\u73b0\\u8ba9\\u6784\\u5efa\\u6b63\\u5e38\\u8fdb\\u884c\\u3002\\u96be\\u70b9\\u5728\\u4e8e\\u6e90\\u7801\\u7684\\u7406\\u89e3\\uff0c\\u4e0e xxx.h \\u63a5\\u53e3\\u6587\\u4ef6\\u4e2d\\u529f\\u80fd\\u51fd\\u6570\\u7684\\u8fd0\\u7528\\uff0c\\u4ece\\u800c\\u5199\\u51fa\\u4f60\\u7684\\u6d4b\\u8bd5\\u6837\\u4f8b\\u3002\\u6bcf\\u4e2a\\u9879\\u76ee\\u7684 xxx.h \\u63a5\\u53e3\\u4e2d\\u51fd\\u6570\\u91cf\\u8005\\u4e0d\\u5c0f\\uff0c\\u5bf9\\u5e94\\u7684\\u6e90\\u7801\\u66f4\\u662f\\u591a\\uff0c\\u7406\\u89e3\\u4e0e\\u6d88\\u5316\\u5e76\\u4e0d\\u5bb9\\u6613\\u3002\\u6e90\\u7801\\u4e2d\\uff0c\\u7ed3\\u6784\\u4f53\\u4e92\\u76f8\\u5d4c\\u5957\\uff0c\\u6216\\u7ed3\\u6784\\u4e2d\\u5305\\u542b\\u51fd\\u6570\\u6307\\u9488\\uff0c\\u5728\\u51fd\\u6570\\u8fd0\\u7528\\u8fd9\\u4e9b\\u7ed3\\u6784\\u4f53\\u65f6\\uff0c\\u53c8\\u67